abstract class PluginzsBlogTag extends BasezsBlogTag
{
  public function getArticlesQuery()
  {
    $ids = $this->getArticles()->getPrimaryKeys();

    /* @var $q Doctrine_Query */
    $q = Doctrine::getTable('zsBlogArticle')->addJoinQuery();

    $q->whereIn('a.id', $ids);

    return $q;
  }
}